import React from "react";

const MobileFooterNav = (navSections) => {
  return (
    <div className="sm:hidden">
      {navSections.menu.map((item, index) => (
        <details key={item._id} open={index === 0 && true}>
          <summary className="list-none sm:pointer-events-none border-b p-5 cursor-pointer">
            <h3 className="text-lg font-semibold text-white tracking-wider">
              {item.displayText}
            </h3>
          </summary>
          <ul className={`p-5 ${index === 0 ? "flex" : ""}`}>
            {item.navLinks.map((subItem) => (
              <li className="p-3" key={subItem._id}>
                <a
                  href={subItem.url}
                  className="text-base text-white hover:underline"
                >
                  {subItem.icon && (
                    <img
                      src={`data:image/svg+xml;utf8,${subItem.icon}`}
                      alt="Company Logo"
                    />
                  )}
                  {subItem.displayText}
                  {subItem.tag && (
                    <div className="rounded-md bg-white ml-2 w-fit inline-block uppercase pr-2 pl-2 text-xs text-black">
                      {subItem.tag}
                    </div>
                  )}
                </a>
              </li>
            ))}
          </ul>
        </details>
      ))}
    </div>
  );
};

export default MobileFooterNav;
