import React from "react";

const DesktopFooterNav = (navSections) => {
  return (
    <div className="mt-12 sm:grid sm:col-span-8 hidden">
      <div className="sm:grid sm:grid-cols-2 md:grid-cols-4 gap-10">
        {navSections.menu.map((item) => (
          <div key={item._id}>
            <h3 className="text-lg font-semibold text-white tracking-wider uppercase">
              {item.displayText}
            </h3>
            <ul className="mt-4 space-y-4">
              {item.navLinks.map((subItem) => (
                <li key={subItem._id}>
                  <a
                    href={subItem.url}
                    className="text-base text-white hover:underline"
                  >
                    {subItem.icon && (
                      <img src={`data:image/svg+xml;utf8,${subItem.icon}`} />
                    )}
                    {subItem.displayText}
                    {subItem.tag && (
                      <div className="rounded-md ml-2 bg-white w-fit inline-block uppercase pr-2 pl-2 text-xs text-black">
                        {subItem.tag}
                      </div>
                    )}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DesktopFooterNav;
