import React from 'react'
import { Spinner } from 'react-bootstrap'

const Loader = () => {
    return (
        <Spinner variant='dark' animation='border' role='status' style={{ width: "100px", height: '100px', margin: 'auto', display: 'block' }}>
            <span className='sr-only'> Loading... </span>
        </Spinner>

    )
}

export default Loader
