import React from "react";
import DesktopFooterNav from "./DesktopFooterNav";
import Logo from "./Logo";
import MobileFooterNav from "./MobileFooterNav";
import { navSections, socialLinks, legalText, baselineLinks } from "./mockData";

const Footer = () => {
  return (
    <footer className="bg-gray-900" aria-labelledby="footer-heading">
      <h2 id="footer-heading" className="sr-only">
        Footer
      </h2>
      <div className="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:py-16 lg:px-8">
        <div className="sm:grid sm:grid-cols-12">
          <div className="sm:mt-12 space-y-8 sm:col-span-4">
            <Logo />
          </div>
          <DesktopFooterNav menu={navSections} />
        </div>
        <MobileFooterNav menu={navSections} />
        <div className="mt-8 flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <p className="text-base text-center text-gray-400 sm:mt-0 order-2 sm:order-2 sm:flex">
            {baselineLinks.map((item) => (
              <a
                key={item._id}
                href={item.url}
                className="text-gray-400 hover:text-gray-300 mb-2 sm:pb-0 sm:mr-5 block"
              >
                {item.displayText}
              </a>
            ))}
            {legalText}
          </p>
          <div className="mt-8 mb-6 sm:mb-0 sm:mt-0 flex space-x-6 order-1 justify-center sm:order-1">
            {socialLinks.map((item) => (
              <a
                key={item._id}
                href={item.url}
                className="text-gray-400 hover:text-gray-300"
              >
                <span className="sr-only">{item.name}</span>
                <img
                  className="h-6 w-6"
                  src={`data:image/svg+xml;utf8,${item.icon}`}
                  alt={item.name}
                />
              </a>
            ))}
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
